import React, { useState } from 'react';
import Plant from './Plant';
import PlantFilter from './PlantFilter';
import Card from '../UI/Card';
import './Plants.css';

const Plants = (props) => {
    const [filteredYear, setFilteredYear] = useState('2023');

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear)
    };

    return (
        <Card className="plants">
            <PlantFilter selected={filteredYear} onChangeFilter={filterChangeHandler}></PlantFilter>
            {props.plants.map(plantInfo => <Plant name={plantInfo.name} firstWater={plantInfo.firstWater} frequency={plantInfo.frequency} />)}
        </Card>
    );
}

export default Plants;
