import React, { useState } from 'react';
import './PlantForm.css';

const PlantForm = () => {
    const [enteredName, setEnteredName] = useState('');
    const [enteredFrequency, setEnteredFrequency] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const nameChangeHandler = (event) => {
        setEnteredName(event.target.value);
    };

    const frequencyChangeHandler = (event) => {
        setEnteredFrequency(event.target.value);
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();

        const plantInfo = {
            name: enteredName,
            frequency: enteredFrequency,
            firstWater: enteredDate
        };

        console.log(plantInfo);
    };

    return (
        <form onSubmit={submitHandler}>
            <div className="new-plant__controls">
                <div className="new-plant__control">
                    <label>
                        Title
                    </label>
                    <input type="text" onChange={nameChangeHandler} />
                </div>
                <div className="new-plant__control">
                    <label>
                        Frequency in Weeks
                    </label>
                    <input type="number" onChange={frequencyChangeHandler} />
                </div>
                <div className="new-plant__control">
                    <label>
                        Date of First Water
                    </label>
                    <input type="date" min="2023-01-01" max="2024-01-01" onChange={dateChangeHandler} />
                </div>
            </div>
            <div className="new-plant__actions"><button type="submit">Add Expense</button></div>
        </form>
    )
};


export default PlantForm;
