import React, { useState } from 'react';
import FirstWater from './FirstWater';
import Card from '../UI/Card';
import './Plant.css'

const Plant = (props) => {
    const [plantName, setName] = useState(props.name);

    const clickHandler = () => {
        setName('Updated!');
        console.log(plantName);
    }

    return (
        <Card className="individual-plant">
            <FirstWater firstWater={props.firstWater} />
            <div className="plant-info">
                <h2>{plantName}</h2>
                <div className="water-frequency">{props.frequency}</div>
            </div>
            <button onClick={clickHandler}>Edit Name</button>
        </Card>
    );

}

export default Plant;
