import React, { useState } from 'react';
import Plant from './Plant';
import PlantFilter from './PlantFilter';
import Card from '../UI/Card';
import './Plants.css';

const Plants = (props) => {
    const [filteredYear, setFilteredYear] = useState('2023');

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear)
    };

    const filteredPlants = props.plants.filter(plant => {
        return plant.firstWater.getFullYear().toString() === filteredYear;
    });

    return (
        <Card className="plants">
            <PlantFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
            {filteredPlants.length === 0 && <p>No plants found.</p>}
            {filteredPlants.length > 0 && filteredPlants.map((plantInfo) => (
                <Plant
                    key={plantInfo.id}
                    name={plantInfo.name}
                    firstWater={plantInfo.firstWater}
                    frequency={plantInfo.frequency}
                />
            ))}
        </Card>
    );
}

export default Plants;
