import React, { useState } from 'react';
import Plant from './Plant';
import PlantFilter from './PlantFilter';
import Card from '../UI/Card';
import './Plants.css';

const Plants = (props) => {
    const [filteredYear, setFilteredYear] = useState('2022');

    const filterChangeHandler = selectedYear => {
        setFilteredYear(selectedYear)
    };

    return (
        <Card className="plants">
            <PlantFilter selected={filteredYear} onChangeFilter={filterChangeHandler}></PlantFilter>
            <Plant name={props.plants[0].name} firstWater={props.plants[0].firstWater} frequency={props.plants[0].frequency} />
            <Plant name={props.plants[1].name} firstWater={props.plants[1].firstWater} frequency={props.plants[1].frequency} />
            <Plant name={props.plants[2].name} firstWater={props.plants[2].firstWater} frequency={props.plants[2].frequency} />
            <Plant name={props.plants[3].name} firstWater={props.plants[3].firstWater} frequency={props.plants[3].frequency} />
        </Card>
    );
}

export default Plants;
